import numpy as np
def stand_deviation(ie_ave_matrix):
    all_ace = np.array(ie_ave_matrix)[:,0]
    standard_deviation = np.std(all_ace)
    M = np.mean(all_ace)
    sum = 0
    for i in range(len(all_ace)):
        dev = np.absolute(all_ace[i] - M)
        sum = sum + round(dev, 2)
    MAD = sum / len(all_ace)
    return standard_deviation, MAD

def average_difference(ie_ave_matrix):
    all_ace = np.array(ie_ave_matrix)[:,0]
    all_diff = []
    all_ave_diff = []
    for i in range(1, len(all_ace)):
        diff = abs(all_ace[i] - all_ace[i-1])
        ave_diff = abs(all_ace[i] - all_ace[i-1])/all_ace[i-1]
        all_diff.append(diff)
        all_ave_diff.append(ave_diff)
    return sum(all_diff)/len(all_diff), sum(all_ave_diff)/len(all_ave_diff)

layers = [0, 2, 4, 6, 8]
def analyze_causal_effect(ie_ave_matrix):
    print("-->standard deviation", stand_deviation(ie_ave_matrix))
    print("-->average difference", average_difference(ie_ave_matrix))
    all_layers = np.array(ie_ave_matrix)[:,1]
    for layer in layers:
        all_per_layer = []
        for i in range(0, len(ie_ave_matrix)):
            if all_layers[i] == layer:
                all_per_layer.append(ie_ave_matrix[i])
        print("For layer {}".format(layer))
        print("Mean ace: {}".format(np.mean(np.array(all_per_layer)[:, 0])))
        print('Standard deviation: {}'.format(stand_deviation(all_per_layer)))
        print('Average difference: {}'.format(average_difference(all_per_layer)))

